mapper.delete:
delete from container


        and port_name=#{port_name}


        and action_date=#{action_date}
    user

mapper.select:
select* from container


        and port_name=#{port_name}


        and action_date between #{action_date_on} and #{action_date_off}


        and lading_id=#{lading_id}


        and action=#{action}

mapper.update:
update container
port_name=#{port_name}, container_id=#{container_id}, container_size=#{container_size}, lading_id=#{lading_id}, container_yard=#{container_yard}, action=#{action}, action_date=#{action_date},
where `index`=#{index}
